__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Evening: Sync Holiday_In-10 when Teds_Room-Light pressed on",
    "parent": 0,
    "comment": "Activate Holiday_In-10 when Teds_Room-Light is physically fast-on during evening hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "47 7F CE 1", "control": "DFON" } },
        { "and": 1 },
        { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "47 87 5B 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "47 87 5B 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "When Teds_Room-Light receives a physical fast-on (COC DFON) during 18:00-23:00, set Holiday_In-10 ramp rate to 1.0 minute (enum 12) then turn it on to 70% so the lights come up smoothly with the room activation. No else actions." 
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Immediate off: Holiday_In-10 when Teds_Room-Light turned off",
    "parent": 0,
    "comment": "Fast off Holiday_In-10 when Teds_Room-Light is physically turned off",
    "if": [
      { "==": { "device": "47 7F CE 1", "control": "DOF" } }
    ],
    "then": [
      { "device": "47 87 5B 1", "command": "DFOF" }
    ],
    "else": [],
    "reasoning": "If Teds_Room-Light is physically turned off (COC DOF), immediately fast-off Holiday_In-10 to ensure the scene turns off without ramp." 
  }
}
__END_NUCORE_ROUTINE__
