__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Keep Shop Motion awake when front landscape lights manually turned on at sunset",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "ZY158_001_1", "control": "DON" } },
      { "and": 1 },
      { "from": { "sunset": 0, "for": { "hours": 2, "minutes": 0, "seconds": 0 } } }
    ],
    "then": [
      { "device": "ZY194_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY194_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 7200, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When the front lights are manually turned on during the two-hour period after sunset, keep the motion sensor awake and set its wake interval to 2 hours so it stays responsive while the area is lit."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Clear Shop Motion keep awake when front landscape lights turned off",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "ZY158_001_1", "control": "DOF" } }
    ],
    "then": [
      { "device": "ZY194_199", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When the landscape lights are turned off, disable Keep Awake on the motion sensor to return it to normal battery-saving behavior."
  }
}
__END_NUCORE_ROUTINE__
