__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn Bedroom Lamp on when garage opens",
    "parent": 0,
    "comment": "Set ramp rate then bring lamp to 70% when garage sensor reports On",
    "if": [
      { "==": { "device": "ZY031_104", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "2E 60 F4 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] },
      { "device": "2E 60 F4 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A COS to On on the garage sensor indicates arrival; apply a 1.0 minute ramp (RR value 12) then set the Bedroom Lamp to 70% using its DON command."
  }
}
__END_NUCORE_ROUTINE__
