__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight: All Off when either assoc turns ON",
    "parent": 0,
    "comment": "If either associated switch sends an ON during the overnight window, send All Off to the Switch All device.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY027_252N002", "control": "DON" } },
        { "or": 1 },
        { "==": { "device": "ZY027_252N003", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY027_183", "command": "ALLOFF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC triggers from the two SC Basic devices (they emit DON when physically turned on). Restrict to the overnight schedule (22:00 to 06:00 next day). If either sends DON in that window, call ALLOFF on the Switch All device. No else actions required." 
  }
}
__END_NUCORE_ROUTINE__
