__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night garage opened -> keep ZY031 awake",
    "parent": 0,
    "comment": "Turn on Keep Awake and set interval when garage switch is turned on overnight; disable when switch turns off",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZB56281_003_1", "control": "DON" } },
        { "and": 1 },
        { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY031_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY031_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 3600, "uom": "58", "precision": 0 } ] }
    ],
    "else": [
      { "device": "ZB56281_003_1", "command": "QUERY", "parameters": [] },
      { "device": "ZY031_199", "command": "DOF", "parameters": [ { "name": "n/a", "value": 0, "uom": "78", "precision": 0 } ] }
    ],
    "reasoning": "Use a COC on the garage switch (DON) combined with an overnight time window to enable Keep Awake (DON) on the ZY031 and set a 3600s wake interval. The else branch queries the switch and disables Keep Awake to restore normal battery operation when not triggered."
  }
}
__END_NUCORE_ROUTINE__
