__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night tamper alert: query security and weather",
    "parent": 0,
    "comment": "",
    "if": [
      { "==": { "device": "ZY032_157", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY032_308", "command": "Query", "parameters": [] },
      { "device": "ZY032_373", "command": "Query", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger when the Tamper Alarm status becomes On during the overnight period (22:00-06:00). Query the Home Security Alarm and Weather Sensor to obtain current states for verification and any further processing."
  }
}
__END_NUCORE_ROUTINE__
