__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Auto-off Output-016 overnight manual on",
    "parent": 0,
    "comment": "When manually turned on overnight, enforce 10-min auto-off",
    "if": [
      { "(": 1 },
      { "==": { "device": "n005_output_16", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      {
        "device": "n005_output_16",
        "command": "DON_WTIME",
        "parameters": [
          { "name": "n/a", "value": 600, "uom": "58", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "When the user physically turns Output-016 on during the overnight window, send Turn On For (600s) to ensure it auto-offs after 10 minutes. Using DON_WTIME enforces the temporary on-duration without changing device defaults."
  }
}
__END_NUCORE_ROUTINE__
