__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday manual ON Output-052 -> Timed ON Output-053",
    "parent": 0,
    "comment": "When Output-052 is physically turned on during weekday mornings, activate Output-053 for 300s.",
    "if": [
      { "==": { "device": "n005_output_52", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "n005_output_53", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 300, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (physical DON) on Output-052 limited to weekdays 06:00-09:00; when observed, send a timed ON (DON_WTIME) to Output-053 for 300 seconds to mirror the manual action." 
  }
}
__END_NUCORE_ROUTINE__
