__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night presence identify when kitchen motion detected",
    "parent": 0,
    "comment": "Run Identify when presence occurs overnight",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"ZB49488_001_1","status":"GV1","value":100,"uom":"78","precision":0 } }
    ],
    "then": [
      { "device":"ZB49488_001_1","command":"IDENTIFY","parameters":[{ "name":"n/a","value":30,"uom":"58","precision":0 }] }
    ],
    "else": [],
    "reasoning": "When the Kitchen Presence (GV1) changes to On during the defined night window (22:00-06:00), run IDENTIFY for 30 seconds to help locate the sensor. Uses the device's Identify command and the documented seconds UOM 58."
  }
}
__END_NUCORE_ROUTINE__
