__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night entry: backlight on when French door opens or front-entry fast-on",
    "parent": 0,
    "comment": "Activate entry lighting overnight on door open or manual fast-on; otherwise turn off backlight",
    "if": [
      { "(": 1 },
        { "==": { "device": "28 F D7 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "38 76 D4 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "38 76 D4 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "25 BD 27 4", "command": "QUERY" },
      { "device": "28 F D7 1", "command": "QUERY" }
    ],
    "else": [
      { "device": "38 76 D4 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 0, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "During overnight hours (22:00–06:00) we want the entry lit when the French door opens or when the front entry is physically fast-on (DFON). The routine sets the backlight to 80% and queries sensors to refresh state; otherwise it turns the backlight off to save energy."
  }
}
__END_NUCORE_ROUTINE__
