__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Turn on Output-004 when Garage Entry F1 after sunset",
    "parent": 0,
    "comment": "",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n005_keypad_1", "control": "GV13" } }
    ],
    "then": [
      { "device": "n005_output_4", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 120, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on the Garage Entry keypad F1 control (COC) but only after sunset to limit daytime activations. Use the output's Turn On For (DON_WTIME) with 120 seconds so the output automatically turns off after the interval."
  }
}
__END_NUCORE_ROUTINE__
