__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night keypad activity -> porch light 5min",
    "parent": 0,
    "comment": "When keypad is used overnight, turn on Output-005 for 300 seconds",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "n005_keypad_3", "status": "ST", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n005_output_5", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 300, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on a COS of the keypad status to True (ST=1) limited to the overnight window (22:00-06:00). Use DON_WTIME on Output-005 for 300 seconds to temporarily light the entry.">
  }
}
__END_NUCORE_ROUTINE__
