__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night manual-on auto-off Output-049",
    "parent": 0,
    "comment": "If Output-049 is manually turned on overnight, limit run to 5 minutes",
    "if": [
      { "(": 1 },
        { "==": { "device": "n005_output_49", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n005_output_49", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 300, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Detect COC (manual ON) for Output-049 during the overnight interval and use DON_WTIME for 300 seconds to ensure it turns off automatically after 5 minutes."
  }
}
__END_NUCORE_ROUTINE__
