__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: Deck on and Family Room to 50% when Driveway Sensor responding",
    "parent": 0,
    "comment": "Turn on deck transformer and set family room lights at sunset if driveway sensor is responding",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY180_1", "status": "ERR", "value": 0, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY028_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "31 5F 95 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "At sunset, ensure the driveway sensor is responding before enabling outdoor lighting. If responding, enable the deck transformer (enum On=100) and set family room lights to 50% using the devices' accepted commands and UOMs."
  }
}
__END_NUCORE_ROUTINE__
