__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday wake: enable keep-awake and set 2h interval",
    "parent": 0,
    "comment": "Enable keep-awake when device wakes during weekday daytime and set interval to 2 hours",
    "if": [
      { "==": { "device": "ZY225_199", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      { "device": "ZY225_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] },
      { "device": "ZY225_199", "command": "SETIV", "parameters": [ { "name": "n/a", "value": 7200, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Trigger on the sensor reporting Is Awake==True during weekday daytime; then send Keep Awake ON (DON with enum uom 78 = 100) and set wake interval to 7200 seconds (2 hours) using SETIV uom 58. This ensures frequent check-ins while awake during active hours."
  }
}
__END_NUCORE_ROUTINE__
