__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening Den switch starts porch music",
    "parent": 0,
    "comment": "When Den-G-H is fast-on or becomes On at sunset, start Sonos Porch at a moderate volume",
    "if": [
      { "(": 1 },
        { "==": { "device": "3E F5 55 4", "control": "DFON" } },
        { "or": 1 },
        { ">=": { "device": "3E F5 55 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "n001_8e9373a5624014", "command": "VOLUME", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "n001_8e9373a5624014", "command": "PLAY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger on a Den-G-H physical fast-on (COC) or its state becoming On (COS) at sunset; then set porch volume to 30% and play. Uses explicit device IDs and accepted Sonos commands."
  }
}
__END_NUCORE_ROUTINE__
