__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Kitchen overnight fast-on sets level and quick ramp",
    "parent": 0,
    "comment": "When someone presses Fast On at night set safe level and fast ramp",
    "if": [
      { "(": 1 },
        { "==": { "device": "70 44 8D 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      {
        "device": "70 44 8D 1",
        "command": "OL",
        "parameters": [
          { "name": "n/a", "value": 15, "uom": "51", "precision": 2 }
        ]
      },
      {
        "device": "70 44 8D 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 28, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use COC for DFON on the Kitchen device and constrain to the overnight schedule (22:00-06:00). Then set On Level to 15% and Ramp Rate to the 0.5s enum index 28."
  }
}
__END_NUCORE_ROUTINE__
