__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Scene Button 1 -> Mini Remote on weekdays",
    "parent": 0,
    "comment": "Apply Mini Remote changes when Scene Button 1 is pressed during weekday daytime",
    "if": [
      { "==": { "device": "ZY009_201N001", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "26 33 AB 1", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger is a COC: Scene Button 1 sending DON. Constrain to weekdays 09:00-17:00 with weekly schedule. Action uses the Mini Remote's accepted command WDU to apply pre-configured changes. No parameters are defined in the DEVICE STRUCTURE, so WDU is sent without parameters."
  }
}
__END_NUCORE_ROUTINE__
