__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Overnight door DFON turns on patio light",
    "parent": 0,
    "comment": "When bedroom door DFON overnight, turn on patio deck light",
    "if": [
      { "(": 1 },
        { "==": { "device": "28 F A7 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY017_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger for the bedroom door (DFON) and restrict to an overnight schedule (22:00-06:00). The action issues DON to ZY017_1 which accepts DON. No parameters are required for the DON command in this use case."
  }
}
__END_NUCORE_ROUTINE__
