__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query devices when Lake view motion after sunset",
    "parent": 0,
    "comment": "When motion is detected at the Lake view after sunset, poll both devices for current status/battery via QUERY.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n006_23595482_m", "control": "DON" } }
    ],
    "then": [
      { "device": "n006_27005601_db", "command": "QUERY", "parameters": [] },
      { "device": "n006_23595482_m", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "User requested a nighttime action triggered by motion from the Lake view sensor. The motion event is emitted by n006_23595482_m (control DON). At sunset ensures it only runs after dark. Actions use the devices' accept command QUERY (n006_27005601_db and n006_23595482_m) to retrieve status/battery values."
  }
}
__END_NUCORE_ROUTINE__
