__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Remote DFON turns on pool scuppers during evening",
    "parent": 0,
    "comment": "Turn on pool scuppers when Patio remote DFON is used between 18:00 and 23:00; otherwise turn them off.",
    "if": [
      { "(": 1 },
        { "==": { "device": "C 47 16 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "18:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "41 1 83 1", "command": "DON" }
    ],
    "else": [
      { "device": "41 1 83 1", "command": "DOF" }
    ],
    "reasoning": "Use a COC (DFON) from the Patio remote as the user-action trigger, but limit the action to an evening time window; ensure scuppers are turned off when the remote is used outside that window."
  }
}
__END_NUCORE_ROUTINE__
