__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Deck lights on at sunset",
    "parent": 0,
    "comment": "Turn on deck lights D, E, F at sunset using Write Changes",
    "if": [
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "4A 97 EC 4", "command": "WDU" },
      { "device": "4A 97 EC 5", "command": "WDU" },
      { "device": "4A 97 EC 6", "command": "WDU" }
    ],
    "else": [],
    "reasoning": "User requested automatic turn-on of the three deck light devices at sunset. The devices accept the Write Changes (WDU) command; we trigger WDU on each device at the sunset schedule. This avoids assuming direct DON/DOF accept commands which are listed as sends rather than accepts."
  }
}
__END_NUCORE_ROUTINE__
