__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Manual ON of Output-031 at sunset -> Output-032 ON 300s",
    "parent": 0,
    "comment": "When Output-031 is manually turned on near sunset, activate Output-032 for 300 seconds.",
    "if": [
      { "==": { "device": "n005_output_31", "control": "DON" } },
      { "and": 1 },
      { "at": { "sunset": 600 } }
    ],
    "then": [
      { "device": "n005_output_32", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 300, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (manual DON) on Output-031 combined with a sunset offset to restrict to within 10 minutes after sunset. Then use DON_WTIME on Output-032 with 300 seconds (uom 58)."
  }
}
__END_NUCORE_ROUTINE__
