__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion triggers notification (Shop 3)",
    "parent": 0,
    "comment": "When Shop 3 motion sensor turns on during night hours, send system custom notification",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY171_155", "status": "ST", "value": 100, "uom": "78", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "n003_controller", "command": "SET_SYS_CUSTOM", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COS check for the Shop 3 motion sensor status == On (100) combined with the overnight schedule (22:00 to 06:00 next day). If triggered, call the Notification Controller's SET_SYS_CUSTOM command to deliver a system-level notification."
  }
}
__END_NUCORE_ROUTINE__
