__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Keep Garage ZY31 awake 2 hours on wake",
    "parent": 0,
    "comment": "When the Garage MultiSensor ZY31 wakes, set wake interval 2h and enable Keep Awake",
    "if": [
      { "==": { "device":"ZY031_199","status":"ST","value":100,"uom":"51","precision":0 } }
    ],
    "then": [
      { "device":"ZY031_199","command":"SETIV","parameters":[{ "name":"n/a","value":2,"uom":"20","precision":0 }] },
      { "device":"ZY031_199","command":"DON","parameters":[{ "name":"n/a","value":100,"uom":"78","precision":0 }] }
    ],
    "else": [],
    "reasoning": "Trigger on COS: Is Awake (ST) becomes True (100). When the sensor wakes, set its Wake Interval to 2 hours (uom 20) and send Keep Awake ON (DON with uom 78 value 100) to keep it awake for that period."
  }
}
__END_NUCORE_ROUTINE__
