__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Paddle FastOn turns on Closet during day",
    "parent": 0,
    "comment": "When paddle fast-on pressed between 06:00-23:00 turn on closet and backlight",
    "if": [
      { "from": { "time":"06:00","to":{ "time":"23:00","day":0 } } },
      { "and": 1 },
      { "==": { "device":"61 89 8E 1","control":"DFON" } }
    ],
    "then": [
      { "device":"50 C5 44 1","command":"DON","parameters":[{ "name":"n/a","value":100,"uom":"51","precision":0 }] },
      { "device":"50 C5 44 1","command":"BL","parameters":[{ "name":"n/a","value":30,"uom":"51","precision":0 }] }
    ],
    "else": [],
    "reasoning":"Activate closet light and set backlight when paddle fast-on is pressed during daytime to provide immediate illumination when user initiates shower."
  }
}
__END_NUCORE_ROUTINE__
