__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Play Sonos at sunset when hall motion detected",
    "parent": 0,
    "comment": "Start playback on motion after sunset",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n002_motion_hall", "status": "ST", "value": 1 } }
    ],
    "then": [
      { "device": "n001_43875cc0b8e014", "command": "UNMUTE", "parameters": [] },
      { "device": "n001_43875cc0b8e014", "command": "VOLUME", "parameters": [ { "name": "n/a", "value": 30, "uom": "51", "precision": 0 } ] },
      { "device": "n001_43875cc0b8e014", "command": "PLAY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger at sunset AND on hall motion (COS to Motion). Unmute and set a safe starting volume before starting playback so audio is audible but not too loud. No else actions required."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 102,
    "name": "Stop Sonos when hall motion stops",
    "parent": 0,
    "comment": "Stop playback when hall reports no motion",
    "if": [
      { "==": { "device": "n002_motion_hall", "status": "ST", "value": 0 } }
    ],
    "then": [
      { "device": "n001_43875cc0b8e014", "command": "STOP", "parameters": [] }
    ],
    "else": [],
    "reasoning": "COS to No Motion stops Sonos to save energy and avoid unnecessary playback when area is vacated."
  }
}
__END_NUCORE_ROUTINE__
