__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Close lower garage door at sunset if open",
    "parent": 0,
    "comment": "Close door at sunset when status is On",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY010_001_1", "status": "ST", "value": 100, "uom": "78", "precision": 0 } }
    ],
    "then": [
      { "device": "ZY010_001_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Run at sunset and if the garage door reports On (open=100), send Off (DOF) to close it."
  }
}
__END_NUCORE_ROUTINE__
