__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Garage open -> beep and query porches",
    "parent": 0,
    "comment": "When garage status becomes On, beep sensor and query both porch devices",
    "if": [
      { "==": { "device": "28 9 E4 4", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "28 9 E4 4", "command": "BEEP" },
      { "device": "ZY012_252N002", "command": "QUERY" },
      { "device": "ZY012_252N003", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger on COS when Garage Door L-Sensor.Hear status transitions to On (100). Beep uses BEEP which the device accepts. Query the two ZY devices using their QUERY accept command to capture current states."
  }
}
__END_NUCORE_ROUTINE__
