__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query power alarm when motion after sunset",
    "parent": 0,
    "comment": "",
    "if": [
      { "at": { "sunset": 600 } },
      { "and": 1 },
      { "==": { "device": "ZY015_155", "control": "DON" } }
    ],
    "then": [
      { "device": "ZY015_309", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger when the motion sensor emits DON within 10 minutes after sunset; then query the power alarm to retrieve its status."
  }
}
__END_NUCORE_ROUTINE__
