__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night motion turns on side gate lights",
    "parent": 0,
    "comment": "",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"n006_18328395_m","control":"DON" } }
    ],
    "then": [
      { "device":"n006_18328395_lt","command":"DON" }
    ],
    "else": [],
    "reasoning": "Turn on Side Gate (Lights) whenever the Side Gate (Motion) sensor reports motion during defined night hours (22:00-06:00) to provide illumination and improve safety."
  }
}
__END_NUCORE_ROUTINE__
