__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night ON -> Write Changes (Guest RM C-D)",
    "parent": 0,
    "comment": "When C-D turns ON during night, notify via Write Changes",
    "if": [
      { "(": 1 },
        { "==": { "device": "54 E9 8A 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "54 E9 8A 2", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Separate routine for device 54 E9 8A 2 so the Write Changes action targets the remote that changed to ON during the defined night window. Uses exact ST value 100 and night schedule 22:00-06:00 (day:1)."
  }
}
__END_NUCORE_ROUTINE__

