{
  "routine": {
    "id": 104,
    "name": "E-F double-tap DFON weekdays 09-17 -> Write Changes",
    "parent": 0,
    "comment": "If E-F receives a double-tap Fast On (DFON, tapCount=2) during weekdays 09:00-17:00, send Write Changes to E-F.",
    "if": [
      { "==": { "device": "54 E9 8A 3", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "54 E9 8A 3", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "COC trigger for device 54 E9 8A 3 with control DFON and tapCount=2 combined with weekly schedule Mon-Fri 09:00-17:00. Targets E-F with its WDU accept command."
  }
}
__END_NUCORE_ROUTINE__

