{
  "routine": {
    "id": 105,
    "name": "Sunrise -15m: If C-D OFF -> Write Changes",
    "parent": 0,
    "comment": "15 minutes before sunrise, if C-D is OFF send Write Changes",
    "if": [
      { "at": { "sunrise": -900 } },
      { "and": 1 },
      { "==": { "device": "54 E9 8A 2", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "54 E9 8A 2", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Schedule at sunrise -900 combined with check that ST == 0 for device 54 E9 8A 2 to send WDU only if it's currently OFF."
  }
}
__END_NUCORE_ROUTINE__

