__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening double-tap: kitchen 70% + entry on",
    "parent": 0,
    "comment": "Double-tap DFON on LR FERN Lamp during weekdays 18:00-23:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "37 33 A9 1", "control": "DFON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "34 7F 1F 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 2 } ] },
      { "device": "71 D E9 1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "34 7F 1F 1", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "Use COC DFON from LR: FERN Lamp to trigger an evening scene only on weekdays (weekly window). Then set the Kitchen to 70% (DON with 70% parameter allowed) and ensure the Entry ornament is ON (DON with enum 100). Else (outside window) just turn the kitchen off to avoid unintended scenes." 
  }
}
__END_NUCORE_ROUTINE__
