__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday Laundry Fast-On -> Set Outside Chand to 50% with 1min ramp",
    "parent": 0,
    "comment": "Triggered by Laundry fast-on during weekday work hours",
    "if": [
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } },
      { "and": 1 },
      { "==": { "device": "4E 44 F7 1", "control": "DFON" } }
    ],
    "then": [
      { "device": "4F 38 29 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] },
      { "device": "4F 38 29 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 12, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When the laundry light is fast-on (user control) during weekdays 09:00-17:00, set the outside chandelier to a moderate mid-level (50%) and apply the 1.0 minute ramp (RR value 12) to smooth transitions and avoid startling." 
  }
}
__END_NUCORE_ROUTINE__
