__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Bookcase DFON -> set backlight 80%",
    "parent": 0,
    "comment": "Trigger on Bookcase DFON to set backlight",
    "if": [
      { "==": { "device": "A E D0 2", "control": "DFON" } }
    ],
    "then": [
      { "device": "A E D0 2", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "A E D0 2", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger: when Gary: Remote Bookcase emits DFON, immediately set its Backlight to 80% and persist the change with Write Changes. Commands used are only those accepted by the device (BL and WDU)."
  }
}
__END_NUCORE_ROUTINE__
