__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Holiday DFON or pre-sunset -> Ted 50% with short fade",
    "parent": 0,
    "comment": "Trigger: Holiday_In-10 DFON or 5 minutes before sunset, condition Holiday_In-9 must be off",
    "if": [
      { "(": 1 },
        { "==": { "device": "47 87 5B 1", "control": "DFON" } },
        { "or": 1 },
        { "at": { "sunset": -300 } },
      { ")": 1 },
      { "and": 1 },
      { "==": { "device": "47 84 10 1", "status": "ST", "value": 0, "uom": "51", "precision": 2 } }
    ],
    "then": [
      {
        "device": "47 7F CE 1",
        "command": "OL",
        "parameters": [
          { "name": "n/a", "value": 50, "uom": "51", "precision": 2 }
        ]
      },
      {
        "device": "47 7F CE 1",
        "command": "RR",
        "parameters": [
          { "name": "n/a", "value": 26, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "User asked to react to a physical change (DFON) or a schedule (5 minutes before sunset) but only when Holiday_In-9 is off. Actions set Ted's room to 50% using the On Level (OL) command and apply a short fade by setting Ramp Rate to the 4.5s enum (value 26) as defined in the device structure."
  }
}
__END_NUCORE_ROUTINE__
