__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn Output-055 on 300s when Output-056 physically turned on after sunset",
    "parent": 0,
    "comment": "Trigger: COC of Output-056 after sunset -> action on Output-055 for 300s",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "n005_output_56", "control": "DON" } }
    ],
    "then": [
      { "device": "n005_output_55", "command": "DON_WTIME", "parameters": [ { "name": "n/a", "value": 300, "uom": "58", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "User requested that a physical turn-on (COC) of Output-056 after sunset should cause Output-055 to be turned on for a specific duration. The trigger combines a sunset schedule and a control event; the action uses DON_WTIME with seconds in uom 58 as defined in the device structure."
  }
}
__END_NUCORE_ROUTINE__
