__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Output-061 COC triggers overnight Output-062 pulse",
    "parent": 0,
    "comment": "When Output-061 is physically turned on at night, pulse Output-062",
    "if": [
      { "==": { "device":"n005_output_61","control":"DON" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"n005_output_62","command":"DON_WTIME","parameters":[ { "name":"n/a","value":120,"uom":"58","precision":0 } ] }
    ],
    "else": [],
    "reasoning": "This routine listens for a Change of Control (COC) on Output-061 using its send control DON. When that physical press occurs during the overnight window (22:00-06:00), it issues a Turn On For (DON_WTIME) to Output-062 for 120 seconds. The schedule prevents activation outside the specified night period."
  }
}
__END_NUCORE_ROUTINE__
