__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Garage Fast-On Weekday Evening Brighten + Outside Plug",
    "parent": 0,
    "comment": "When the garage light receives a Fast On during weekday evenings, force full brightness and enable outside plug.",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY220_1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "17:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "ZY220_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "51", "precision": 0 } ] },
      { "device": "ZY158_002_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "User manually activates garage Fast On; during specified weekday evening window we ensure strong illumination by setting garage to 100% and turning on the outside plug for exterior lighting. Using DFON as a COC trigger avoids accidental automation outside the time window."
  }
}
__END_NUCORE_ROUTINE__
