__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn on relay when garage side door opens overnight",
    "parent": 0,
    "comment": "Turn on ZY010 Relay when Garage Side Door Sensor.Open changes to On between 22:00 and 06:00; otherwise ensure it's off.",
    "if": [
      { "(": 1 },
        { "==": { "device": "25 BE 89 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZY010_003_1", "command": "DON", "parameters": [] }
    ],
    "else": [
      { "device": "ZY010_003_1", "command": "DOF", "parameters": [] }
    ],
    "reasoning": "Use a COS on Garage Side Door Sensor.Open status == On combined with an overnight schedule (22:00->06:00 day roll) to activate the relay only during night hours. Else branch ensures the relay is turned off when the door is not open in that window."
  }
}
__END_NUCORE_ROUTINE__
