__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night manual closet ON -> mobile alarm",
    "parent": 0,
    "comment": "Notify when closet light is manually turned on overnight",
    "if": [
      { "==": { "device": "ZY016_1", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      {
        "device": "n001_udmobile",
        "command": "GV10",
        "parameters": [
          { "name": "Group", "value": 0, "uom": "25", "precision": 0 },
          { "name": "Sound", "value": 7, "uom": "25", "precision": 0 },
          { "name": "Content", "value": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Trigger on physical control (COC) of ZY016_1 using its On (DON) command during overnight schedule; then call UD Mobile GV10 with Alarm Burglar sound (value 7) and content id 1 to alert the user."
  }
}
__END_NUCORE_ROUTINE__
