__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night DFON: alert sensor and full backlight",
    "parent": 0,
    "comment": "On fast-on of Front Entry overnight, alert nearby sensor and illuminate entry",
    "if": [
      { "from": { "time":"22:00", "to":{ "time":"06:00", "day":1 } } },
      { "and": 1 },
      { "==": { "device":"38 76 D4 1", "control":"DFON" } }
    ],
    "then": [
      { "device":"25 BD 27 4", "command":"BEEP" },
      { "device":"38 76 D4 1", "command":"BL", "parameters":[{ "name":"n/a", "value":100, "uom":"51", "precision":0 }] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on the Front Entry combined with an overnight schedule (22:00->06:00 day wrap). When a human fast-ons the entry at night, beep the Font Door Sensor.HB as an audible alert and set the Front Entry backlight to 100% for visibility. Beep requires no parameter here; backlight uses the documented 0-100 % uom id 51."
  }
}
__END_NUCORE_ROUTINE__
