__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1001,
    "name": "Keep Wake Up device awake when SC Basic Control turned on during work hours",
    "parent": 0,
    "comment": "Enable Keep Awake on ZY015_199 when ZY015_251 is turned on during weekday 09:00-17:00",
    "if": [
      { "==": { "device": "ZY015_251", "control": "DON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY015_199", "command": "DON", "parameters": [ { "name": "n/a", "value": 100, "uom": "78", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "When the SC Basic Control (ZY015_251) is physically turned on during weekday work hours, keep the Wake Up node (ZY015_199) awake so it maintains connectivity and timely communication. The weekly window limits this behavior to work hours." 
  }
}
__END_NUCORE_ROUTINE__
