__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening: Turn on garage when walkway manually activated",
    "parent": 0,
    "comment": "If either walkway switch is physically turned on during nightly hours, enable garage outside lights for safety.",
    "if": [
      { "(": 1 },
        { "==": { "device": "40 E6 CA 1", "control": "DFON" } },
        { "or": 1 },
        { "==": { "device": "38 75 B8 1", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "sun,mon,tue,wed,thu,fri,sat", "from": { "time": "20:00", "to": { "time": "06:00" } } } }
    ],
    "then": [
      { "device": "40 E7 63 1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC (physical control) from either walkway device during defined nightly hours to trigger the garage lights so manual activation by a user results in immediate safe lighting." 
  }
}
__END_NUCORE_ROUTINE__
