__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Turn on backyard lights on motion at night",
    "parent": 0,
    "comment": "Turn on both backyard lights when motion is detected during night",
    "if": [
      { "from": { "sunset": 0, "to": { "sunrise": 0, "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "n006_19442790_m", "control": "DON" } }
    ],
    "then": [
      { "device": "n006_30221886_lt", "command": "DON" },
      { "device": "n006_19442790_lt", "command": "DON" }
    ],
    "else": [],
    "reasoning": "When the East Backyard motion sensor emits a Motion (DON) event during the night window (sunset -> sunrise), the routine turns on both Backyard (n006_30221886_lt) and East Backyard lights (n006_19442790_lt) using their accepted DON command to illuminate the area."
  }
}
__END_NUCORE_ROUTINE__
