__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight guest lights on after garage fast-on",
    "parent": 0,
    "comment": "When garage is fast-on overnight, set guest lights RR then set to low level",
    "if": [
      { "==": { "device": "4F 6E 4D 1", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"55 CB A9 1","command":"RR","parameters":[{ "name":"n/a","value":28,"uom":"25","precision":0 }] },
      { "device":"55 CB A9 1","command":"DON","parameters":[{ "name":"n/a","value":15,"uom":"51","precision":0 }] }
    ],
    "else": [],
    "reasoning": "Use COC (garage DFON) plus overnight window to avoid daytime triggers. Set Ramp Rate first (RR enum value 28 = 0.5s) then send DON with 15% brightness to ensure smooth transition."
  }
}
__END_NUCORE_ROUTINE__
