__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Scheduled watering Tue/Thu/Fri 09:00 if enabled and water available",
    "parent": 0,
    "comment": "Run Plants START for 10 minutes when enabled and enough available water",
    "if": [
      { "weekly": { "days": "tue,thu,fri", "from": { "time": "09:00", "to": { "time": "09:00" } } } },
      { "and": 1 },
      { "==": { "device": "n003_083a88eb65231", "status": "GV0", "value": 1, "uom": "2", "precision": 0 } },
      { "and": 1 },
      { ">=": { "device": "n003_083a88eb65231", "status": "GV2", "value": 100, "uom": "45", "precision": 2 } }
    ],
    "then": [
      { "device": "n003_083a88eb65231", "command": "START", "parameters": [ { "name": "n/a", "value": 100, "uom": "45", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Weekly schedule at 09:00 on Tue/Thu/Fri. Require Enabled (GV0 == 1). Require Available Water (GV2) >= 1.00 minutes; precision 2 so value=100. START uses precision 1; to run 10.0 minutes send value=100."
  }
}
__END_NUCORE_ROUTINE__
