__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Disable Office_Controller.4 backlight when rain >= 5mm",
    "parent": 0,
    "comment": "Disable backlight during measurable rain",
    "if": [
      { ">=": { "device": "n001_weather", "status": "GV6", "value": 5, "uom": "82", "precision": 0 } }
    ],
    "then": [
      { "device": "0 6D 74 4", "command": "BL", "parameters": [ { "name": "n/a", "value": 0, "uom": "51", "precision": 0 } ] },
      { "device": "0 6D 74 4", "command": "WDU", "parameters": [] }
    ],
    "else": [],
    "reasoning": "When Rain Today (GV6, mm) reaches or exceeds 5 mm, turn Office_Controller.4 backlight to 0 and persist via Write Changes to reduce light/use during rain. Uses explicit GV6 property and BL/WDU commands from the device structure."
  }
}
__END_NUCORE_ROUTINE__
